mapper.insert:
INSERT INTO USER (
	  USER_ID
	, USER_PW
	, USER_NAME
	, USER_TEL
	, USER_JOIN_DATE
	, USER_POINT
	, USER_IMG_PATH
	, USER_HAS_IMG
) VALUES (
	  #{userId}
	, #{userPw}
	, #{userName}
	, #{userTel}
	, NOW()
	, 0
	, '/images/main/default_user.png'
	, 0
)

mapper.select:
SELECT *
  FROM USER
 WHERE USER_ID = #{userId} AND USER_PW = #{userPw}

mapper.update:
UPDATE USER
   SET USER_AUTO_LOGIN_ID = #{userAutoLoginId}
     , USER_AUTO_LOGIN_EXPIRED = #{userAutoLoginExpired}
 WHERE USER_ID = #{userId}
